import React, { useState } from 'react';
import './Client.css';

const Client = () => {
  const [showNewClientForm, setShowNewClientForm] = useState(false);
  const [newClient, setNewClient] = useState({
    type: '',
    reference: '',
    entreprise: '',
    prenom: '',
    nom: '',
    email: '',
    numTel: ''
  });
  const [clients, setClients] = useState([]); // Nouvel état pour stocker les clients enregistrés
  const [clientCount, setClientCount] = useState(0); // Nouvel état pour le compteur de clients

  const handleNewClient = () => {
    setShowNewClientForm(true);
  };

  const handleCancel = () => {
    setShowNewClientForm(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewClient(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSaveClient = () => {
    // Incrémenter le compteur de clients
    setClientCount(prevCount => prevCount + 1);

    // Créer un nouvel objet client avec les informations saisies dans le formulaire
    const client = {
      numero: clientCount + 1,
      nom: newClient.nom,
      prenom: newClient.prenom,
      facturesEnAttente: 0, // Valeur par défaut
      facturesPayees: 0 // Valeur par défaut
    };

    // Ajouter le nouveau client à la liste des clients enregistrés
    setClients(prevClients => [...prevClients, client]);

    // Réinitialiser le formulaire de nouveau client
    setNewClient({
      type: '',
      reference: '',
      entreprise: '',
      prenom: '',
      nom: '',
      email: '',
      numTel: ''
    });

    // Masquer le formulaire de nouveau client
    setShowNewClientForm(false);
  };

  return (
    <div className="client-page">
      <div className="left-top">
        <input type="text" placeholder="Recherche..." />
        <button onClick={handleNewClient} className="green-button">Nouveau client</button>
      </div>
      <div className="main-content">
        {showNewClientForm ? (
          <div className="new-client-form">
            <h2>Nouveau client</h2>
            <div className="form-group">
              <label>Type de client :</label>
              <input type="text" name="type" value={newClient.type} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>Référence client: </label>
              <input type="text" name="reference" value={newClient.reference} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>Nom de l'entreprise: </label>
              <input type="text" name="reference" value={newClient.reference} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>Nom: </label>
              <input type="text" name="reference" value={newClient.reference} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>Prenom: </label>
              <input type="text" name="reference" value={newClient.reference} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>Cin: </label>
              <input type="text" name="reference" value={newClient.reference} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>Email :</label>
              <input type="text" name="email" value={newClient.email} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>Numéro de téléphone:</label>
              <input type="text" name="numTel" value={newClient.numTel} onChange={handleChange} />
            </div>
            {/* Ajouter d'autres champs du formulaire ici */}
            <div className="form-group">
              <button onClick={handleCancel} className="blue-button">Annuler</button>
              <button onClick={handleSaveClient} className="blue-button">Enregistrer</button>
            </div>
          </div>
        ) : (
          <div>
            <h1>Clients</h1>
            <p>Clients enregistrés avec votre abonnement : {clients.length}/5</p>
            <table>
              <thead>
                <tr>
                  <th>Numéro</th>
                  <th>Client</th>
                  <th>Factures en attente</th>
                  <th>Factures payées</th>
                </tr>
              </thead>
              <tbody>
                {clients.map((client, index) => (
                  <tr key={index}>
                    <td>{client.numero}</td>
                    <td>{`${client.prenom} ${client.nom}`}</td>
                    <td>{client.facturesEnAttente}</td>
                    <td>{client.facturesPayees}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );

};

export default Client;
