import React, { useState } from 'react';

const ajoutDocument= () => {
  const [invoiceData, setInvoiceData] = useState({
    companyName: '',
    invoiceNumber: '',
    dueDate: '',
    items: [{ description: '', quantity: 0, price: 0 }],
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setInvoiceData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleItemChange = (index, event) => {
    const { name, value } = event.target;
    const updatedItems = invoiceData.items.map((item, i) => {
      if (i === index) {
        return { ...item, [name]: value };
      }
      return item;
    });
    setInvoiceData((prevState) => ({
      ...prevState,
      items: updatedItems,
    }));
  };

  const addItem = () => {
    setInvoiceData((prevState) => ({
      ...prevState,
      items: [...prevState.items, { description: '', quantity: 0, price: 0 }],
    }));
  };

  const removeItem = (index) => {
    const updatedItems = invoiceData.items.filter((item, i) => i !== index);
    setInvoiceData((prevState) => ({
      ...prevState,
      items: updatedItems,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Generate invoice document or perform other actions here
    console.log('Invoice Data:', invoiceData);
  };

  return (
    <div>
      <h2>Invoice Form</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="companyName">Company Name:</label>
          <input
            type="text"
            id="companyName"
            name="companyName"
            value={invoiceData.companyName}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="invoiceNumber">Invoice Number:</label>
          <input
            type="text"
            id="invoiceNumber"
            name="invoiceNumber"
            value={invoiceData.invoiceNumber}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="dueDate">Due Date:</label>
          <input
            type="date"
            id="dueDate"
            name="dueDate"
            value={invoiceData.dueDate}
            onChange={handleChange}
          />
        </div>
        <h2>Items</h2>
        {invoiceData.items.map((item, index) => (
          <div key={index}>
            <label htmlFor={`description-${index}`}>Description:</label>
            <input
              type="text"
              id={`description-${index}`}
              name={`description-${index}`}
              value={item.description}
              onChange={(event) => handleItemChange(index, event)}
            />
            <label htmlFor={`quantity-${index}`}>Quantity:</label>
            <input
              type="number"
              id={`quantity-${index}`}
              name={`quantity-${index}`}
              value={item.quantity}
              onChange={(event) => handleItemChange(index, event)}
            />
            <label htmlFor={`price-${index}`}>Price:</label>
            <input
              type="number"
              id={`price-${index}`}
              name={`price-${index}`}
              value={item.price}
              onChange={(event) => handleItemChange(index, event)}
            />
            <button onClick={() => removeItem(index)}>Remove Item</button>
          </div>
        ))}
        <button onClick={addItem}>Add Item</button>
        <button type="submit">Generate Invoice</button>
      </form>
    </div>
  );
};

export default ajoutDocument;
