// export default Facture;
import React, { useState } from 'react';
import CreeFacture from './CreeFacture'; // Import du composant CreeFacture

const Facture = () => {
  const [showNewFactureForm, setShowNewFactureForm] = useState(false);

  const handleNewFacture = () => {
    setShowNewFactureForm(true); // Affiche l'interface de création de facture
  };

  return (
    <div className="facture-page">
      <div className="left-side">
        <div className="left-top">
          <input type="text" placeholder="Recherche..." />
          <button onClick={handleNewFacture} className="green-button">Nouvelle facture</button>
        </div>
        <div className="main-content">
          {showNewFactureForm && <CreeFacture />} {/* Affiche l'interface de création de facture si showNewFactureForm est vrai */}
        </div>
      </div>
      <div className="right-side">
        <h1>Factures</h1>
        <p>Sélectionnez une facture dans la liste de gauche pour voir ses détails</p>
      </div>
    </div>
  );
};

export default Facture;
