import React, { useState } from 'react';
import './Client.css';

const Client = () => {
  const [selectedClient, setSelectedClient] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [clients, setClients] = useState([]); // Vos clients

  // Fonction pour sélectionner un client
  const selectClient = (client) => {
    setSelectedClient(client);
  };

  // Fonction pour gérer le changement dans le champ de recherche
  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
    // Vous pouvez implémenter la logique de recherche ici
  };

  // Fonction pour importer le client
  const importClient = () => {
    // Implémentez votre logique d'importation ici
    console.log('Client importé :', selectedClient);
  };

  return (
    <div className="client-page">
      <div className="left-side">
        <input
          type="text"
          value={searchQuery}
          onChange={handleSearchChange}
          placeholder="Recherche..."
        />
        <table>
          <thead>
            <tr>
              <th>Numéro</th>
              <th>Nom</th>
              <th>Prénom</th>
              {/* Ajoutez d'autres colonnes si nécessaire */}
            </tr>
          </thead>
          <tbody>
            {clients.map((client, index) => (
              <tr key={index} onClick={() => selectClient(client)}>
                <td>{client.numero}</td>
                <td>{client.nom}</td>
                <td>{client.prenom}</td>
                {/* Ajoutez d'autres colonnes si nécessaire */}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="right-side">
        {selectedClient ? (
          <div>
            <h2>Détails du client</h2>
            <p>Nom: {selectedClient.nom}</p>
            <p>Prénom: {selectedClient.prenom}</p>
            {/* Ajoutez d'autres détails du client ici */}
            <button onClick={importClient}>Importer client</button>
          </div>
        ) : (
          <div>
            <p>Sélectionnez un client dans la liste de gauche pour voir ses détails.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Client;
