import React from 'react';
import './Accueil.css'; // Assurez-vous d'avoir un fichier CSS pour styliser votre page d'accueil

const Accueil = () => {
  return (
    <div className="accueil-page">
      <div className="left-section">
        <div className="circle-chart">
          <div className="circle blue">0,00DT</div>
          <div className="circle green">0,00DT</div>
          <div className="circle red">0,00DT</div>
        </div>
      </div>
      <div className="right-section">
        <h2>Factures ouvertes</h2>
        <button>Créer votre première facture</button>
        {/* Section cachée */}
        <div className="hidden-section">
          <h3>Non échues</h3>
          <h3>En retard (- de 30 jours)</h3>
          <h3>En retard (+ de 30 jours)</h3>
        </div>
      </div>
    </div>
  );
};

export default Accueil;
