import React, { useState } from 'react';
//import './App.css';

function compte() {
  const [selectedLink, setSelectedLink] = useState(null);

  const handleLinkClick = (link) => {
    setSelectedLink(link);
  };

  const renderCompanyInfo = () => (
    <div className="info">
      <h2>Coordonnées de l'entreprise</h2>
      <div className="field">
        <label>Logo de l'entreprise</label>
        <input type="file" />
      </div>
      <div className="field">
        <label>Nom de l'entreprise</label>
        <input type="text" placeholder="Entrez le nom de votre entreprise" />
      </div>
      <div className="field">
        <label>E-mail de contact</label>
        <input type="email" placeholder="Entrez l'e-mail de contact de l'entreprise" />
      </div>
      <div className="field">
        <label>Numéro de SIRET</label>
        <input type="text" placeholder="Entrez le numéro de SIRET de l'entreprise" />
      </div>
      <div className="field">
        <label>Numéro de TVA</label>
        <input type="text" placeholder="Entrez le numéro de TVA de l'entreprise" />
      </div>
      <div className="field">
        <label>TVA par défaut</label>
        <input type="text" placeholder="Entrez le taux de TVA par défaut de l'entreprise" />
      </div>
      <button>Enregistrer</button>
    </div>
  );

  const renderBillingSettings = () => (
    <div className="info">
      <h2>Paramètres de facturation</h2>
      {/* Ajouter les paramètres de facturation ici */}
    </div>
  );

  const renderUserAccount = () => (
    <div className="info">
      <h2>Compte utilisateur</h2>
      {/* Ajouter les paramètres du compte utilisateur ici */}
    </div>
  );

  return (
    <div className="container">
      <div className="links">
        <ul>
          <li onClick={() => handleLinkClick('companyInfo')}>Coordonnées de l'entreprise</li>
          <li onClick={() => handleLinkClick('billingSettings')}>Paramètres de facturation</li>
          <li onClick={() => handleLinkClick('userAccount')}>Compte utilisateur</li>
        </ul>
      </div>
      <div className="content">
        {selectedLink === 'companyInfo' && renderCompanyInfo()}
        {selectedLink === 'billingSettings' && renderBillingSettings()}
        {selectedLink === 'userAccount' && renderUserAccount()}
      </div>
    </div>
  );
}

export default compte;
