import React, { useState } from 'react';

const ClientPage = () => {
  const [showNewClientForm, setShowNewClientForm] = useState(false);
  const [newClientData, setNewClientData] = useState({
    type: 'Par défaut',
    reference: '',
    entreprise: '',
    prenom: '',
    nom: '',
    email: '',
    numTel: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewClientData({ ...newClientData, [name]: value });
  };

  const handleNewClientSubmit = () => {
    // Logique pour enregistrer le nouveau client
  };

  const handleCancel = () => {
    setShowNewClientForm(false);
  };

  return (
    <div className="client-page">
      <div className="search-bar">
        {/* Zone de recherche */}
        <input type="text" placeholder="Recherche..." />
        {/* Bouton vert pour ajouter un nouveau client */}
        <button onClick={() => setShowNewClientForm(true)}>Nouveau client</button>
      </div>

      {showNewClientForm && (
        <div className="new-client-form">
          {/* Formulaire pour ajouter un nouveau client */}
          <h2>Nouveau client</h2>
          <label>Type de client:</label>
          <input type="text" name="type" value={newClientData.type} onChange={handleInputChange} />
          <label>Référence client:</label>
          <input type="text" name="reference" value={newClientData.reference} onChange={handleInputChange} />
          <label>Nom de l'entreprise:</label>
          <input type="text" name="entreprise" value={newClientData.entreprise} onChange={handleInputChange} />
          <label>Prénom:</label>
          <input type="text" name="prenom" value={newClientData.prenom} onChange={handleInputChange} />
          <label>Nom de famille:</label>
          <input type="text" name="nom" value={newClientData.nom} onChange={handleInputChange} />
          <label>Email:</label>
          <input type="text" name="email" value={newClientData.email} onChange={handleInputChange} />
          <label>Numéro de téléphone:</label>
          <input type="text" name="numTel" value={newClientData.numTel} onChange={handleInputChange} />
          <div className="buttons">
            <button className="save-button" onClick={handleNewClientSubmit}>Enregistrer</button>
            <button className="cancel-button" onClick={handleCancel}>Annuler</button>
          </div>
        </div>
      )}

      <div className="clients-list">
        <h2>Clients</h2>
        {/* Liste des clients */}
        {/* Vous pouvez remplir cette partie avec vos données de clients */}
      </div>

      <div className="subscription-info">
        <p>Vos clients enregistrés avec votre abonnement : 0/5</p>
      </div>

      <div className="table">
        {/* Tableau avec les colonnes : Numero, Client, Factures en attentes, Factures payées */}
        {/* Vous pouvez remplir cette partie avec vos données de tableau */}
      </div>
    </div>
  );
};

export default ClientPage;
