export const getSalesData = async () => {
    try {
      const response = await fetch('url_de_votre_api');
      
      if (!response.ok) {
        throw new Error('Erreur lors de la récupération des données de ventes');
      }
  
      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Erreur lors de la récupération des données de ventes :', error);
      throw error;
    }
  };
