import React, { useState } from 'react';
import './creeFacture.css';

const CreeFacture = () => {
  const [clientsVisible, setClientsVisible] = useState(false);
  const [message, setMessage] = useState('');
  const [facture, setFacture] = useState({
    numero: '',
    date: '',
    conditionsPaiement: '',
    echéance: '',
    engagement: '',
    codeService: ''
  });
  const [ligneFacture, setLigneFacture] = useState([]);

  const toggleClients = () => {
    setClientsVisible(!clientsVisible);
  };

  const handleSendMessage = () => {
    // Code pour envoyer le message
  };

  const handleAddLine = () => {
    // Code pour ajouter une ligne dans le tableau de facture
  };

  return (
    <div>
      {/* Barre de navigation */}
      <nav>
        <button>Enregistrer</button>
        <button>Voir l'aperçu</button>
      </nav>

      {/* Clients */}
      <div>
        <button onClick={toggleClients}>Clients</button>
        {clientsVisible && (
          <div>
            {/* Liste des clients */}
            {/* Code pour afficher la liste des clients */}
          </div>
        )}
      </div>

      {/* Facture */}
      <div className="facture">
        <div>
          <div>Numéro de facture: {facture.numero}</div>
          <div>Date de facturation: {facture.date}</div>
          <div>Conditions de paiement: {facture.conditionsPaiement}</div>
          <div>Échéance: {facture.echéance}</div>
          <div>Numéro d'engagement: {facture.engagement}</div>
          <div>Code service: {facture.codeService}</div>
        </div>

        {/* Tableau de facture */}
        <div className="table-facture">
          <div className="colon-facture">
            <div>Description</div>
            <div>Type de produit</div>
            <div>Date</div>
            <div>Qté</div>
            <div>Unité</div>
            <div>Prix</div>
            <div>TVA</div>
            <div>Montant</div>
          </div>
          {/* Boucle à travers les lignes de facture et afficher ici */}
        </div>

        <button onClick={handleAddLine}>Ajouter une ligne</button>
      </div>

      {/* Message */}
      <div>
        <label>Message:</label>
        <textarea
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <button onClick={handleSendMessage}>Envoyer</button>
      </div>
    </div>
  );
};

export default CreeFacture;
