import React, { useState } from 'react';
import './creeFacture.css';

const CreeFacture = () => {
  const [clientsVisible, setClientsVisible] = useState(false);
  const [message, setMessage] = useState('');
  const [facture, setFacture] = useState({
    numero: '',
    date: '',
    conditionsPaiement: '',
    echéance: '',
    engagement: '',
    codeService: ''
  });
  const [ligneFacture, setLigneFacture] = useState([]);

  const toggleClients = () => {
    setClientsVisible(!clientsVisible);
  };

  const handleSendMessage = () => {
    // Code pour envoyer le message
  };

  const handleAddLine = () => {
    // Code pour ajouter une ligne dans le tableau de facture
  };

  return (
    <div>
      {/* Barre de navigation */}
      <nav>
        <button>Enregistrer</button>
        <button>Voir l'aperçu</button>
      </nav>

      {/* Clients */}
      <div>
        <button onClick={toggleClients}>Clients</button>
        {clientsVisible && (
          <div>
            {/* Liste des clients */}
            {/* Code pour afficher la liste des clients */}
          </div>
        )}
      </div>

      {/* Facture */}
      <div>
        <div className="facture">
          <table>
            <tbody>
              <tr>
                <td>Numéro de facture</td>
                <td>{facture.numero}</td>
              </tr>
              <tr>
                <td>Date de facturation</td>
                <td>{facture.date}</td>
              </tr>
              <tr>
                <td>Conditions de paiement</td>
                <td>{facture.conditionsPaiement}</td>
              </tr>
              <tr>
                <td>Échéance</td>
                <td>{facture.echéance}</td>
              </tr>
              <tr>
                <td>Numéro d'engagement</td>
                <td>{facture.engagement}</td>
              </tr>
              <tr>
                <td>Code service</td>
                <td>{facture.codeService}</td>
              </tr>
            </tbody>
          </table>
        </div>

        {/* Tableau de facture */}
        <table>
          <thead>
            <tr>
              <th>Description</th>
              <th>Type de produit</th>
              <th>Date</th>
              <th>Qté</th>
              <th>Unité</th>
              <th>Prix</th>
              <th>TVA</th>
              <th>Montant</th>
            </tr>
          </thead>
          <tbody>
            {/* Boucle à travers les lignes de facture et afficher ici */}
          </tbody>
        </table>

        <button onClick={handleAddLine}>Ajouter une ligne</button>
      </div>

      {/* Message */}
      <div>
        <label>Message:</label>
        <textarea
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <button onClick={handleSendMessage}>Envoyer</button>
      </div>
    </div>
  );
};

export default CreeFacture;
