import React, { useState } from 'react';


const creeFacture= () => {
  const [clientsVisible, setClientsVisible] = useState(false);
  const [message, setMessage] = useState('');
  const [facture, setFacture] = useState({
    numero: '',
    date: '',
    conditionsPaiement: '',
    echéance: '',
    engagement: '',
    codeService: ''
  });
  const [ligneFacture, setLigneFacture] = useState([]);

  const toggleClients = () => {
    setClientsVisible(!clientsVisible);
  };

  const handleSendMessage = () => {
    // Code pour envoyer le message
  };

  const handleAddLine = () => {
    // Code pour ajouter une ligne dans le tableau de facture
  };

  return (
    <div>
      {/* Navbar */}
      <nav>
        <button>Enregistrer</button>
        <button>Voir l'aperçu</button>
      </nav>

      {/* Clients */}
      <div>
        <button onClick={toggleClients}>Clients</button>
        {clientsVisible && (
          <div>
            {/* Liste des clients */}
            {/* Code pour afficher la liste des clients */}
          </div>
        )}
      </div>

      {/* Facture */}
      <div>
        <div>
          <label>Numéro de facture:</label>
          <input
            type="text"
            value={facture.numero}
            onChange={(e) => setFacture({ ...facture, numero: e.target.value })}
          />
          {/* Ajouter d'autres champs de la facture ici */}
        </div>

        {/* Tableau de facture */}
        <table>
          <thead>
            <tr>
              <th>Description</th>
              <th>Type de produit</th>
              <th>Date</th>
              <th>Qté</th>
              <th>Unité</th>
              <th>Prix</th>
              <th>TVA</th>
              <th>Montant</th>
            </tr>
          </thead>
          <tbody>
            {/* Boucle à travers les lignes de facture et afficher ici */}
          </tbody>
        </table>

        <button onClick={handleAddLine}>Ajouter une ligne</button>
      </div>

      {/* Message */}
      <div>
        <label>Message:</label>
        <textarea
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <button onClick={handleSendMessage}>Envoyer</button>
      </div>
    </div>
  );
};

export default creeFacture;
