import React, { useState } from 'react';
import './produit.css';

const produit = () => {
  const [showNewproduitForm, setShowNewproduitForm] = useState(false);
  const [newproduit, setNewproduit] = useState({
      nom: '',
      typedeproduit: '',
      unité: '',
      prix: '',
      tva: '',
 
  });
  const [produit, setproduit] = useState([]); 
  const [produitCount, setproduitCount] = useState(0); 

  const handleNewproduit = () => {
    setShowNewproduitForm(true);
  };

  const handleCancel = () => {
    setShowNewproduitForm(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewproduit(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSaveproduit = () => {
    setproduitCount(prevCount => prevCount + 1);

    const produit = {
      Produit: produitCount + 1,
      PrixHT : 0, 
      TVA: 0, 
      PRIXTTC : 0,
     
    };

    setproduit(prevproduit => [...prevproduit, produit]);

    setNewproduit({
      nom: '',
      typedeproduit: '',
      unité: '',
      prix: '',
      tva: '',
    });

    setShowNewproduitForm(false);
  };

  return (
    <div className="produit-page">
      <div className="left-side">
        <div className="left-top">
          <input type="text" placeholder="Recherche..." />
          <button onClick={handleNewproduit} className="green-button">produit</button>
        </div>
        <div className="main-content">
          {showNewproduitForm ? (
            <div className="new-produit-form">
              <h2>Nouveau produit</h2>
              <div className="form-group">
                <label>Nom :</label>
                <input type="text" name="type" value={newproduit.type} onChange={handleChange} />
              </div>
              <div className="form-group">
              <label>Type de produit: </label>
              <input type="text" name="reference" value={newproduit.reference} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>Unité: </label>
              <input type="text" name="reference" value={newproduit.reference} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>Prix: </label>
              <input type="text" name="reference" value={newproduit.reference} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>TVA: </label>
              <input type="text" name="reference" value={newproduit.reference} onChange={handleChange} />
            </div>
              <div className="form-group">
                <button onClick={handleCancel} className="blue-button">Annuler</button>
                <button onClick={handleSaveproduit} className="blue-button">Enregistrer</button>
              </div>
            </div>
          ) : (
            <div>
              <h1>Produits et Services</h1>
              <table>
                <thead>
                  <tr>
                    <th>Produits</th>
                    <th>Prix Ht</th>
                    <th>TVA</th>
                    <th>Prix TTC</th>
                  </tr>
                </thead>
                <tbody>
                  {produit.map((produit, index) => (
                    <tr key={index}>
                      <td>{produit.numero}</td>
                      <td>{`${produit.prenom} ${produit.nom}`}</td>
                      <td>{produit.facturesEnAttente}</td>
                      <td>{produit.facturesPayees}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
      <div className="right-side">
        <h1>Produits et Services</h1>
        <p>Sélectionnez un produit ou service dans la liste de gauche pour voir ses détails</p>
        <button>Importer produit</button>
      </div>
    </div>
  );
};

export default produit ;
