import React, { useState } from 'react';
import './Client.css';

const Client = () => {
  const [showNewClientForm, setShowNewClientForm] = useState(false);
  const [newClient, setNewClient] = useState({
    type: '',
    reference: '',
    entreprise: '',
    prenom: '',
    nom: '',
    email: '',
    numTel: ''
  });

  const handleNewClient = () => {
    setShowNewClientForm(true);
  };

  const handleCancel = () => {
    setShowNewClientForm(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewClient(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  return (
    <div className="client-page">
      <div className="left-top">
        <input type="text" placeholder="Recherche..." />
        <button onClick={handleNewClient} className="green-button">Nouveau client</button>
      </div>
      <div className="main-content">
        {showNewClientForm ? (
          <div className="new-client-form">
            <h2>Nouveau client</h2>
            {/* Formulaire du nouveau client */}
            <div className="form-group">
              <label>Type de client :</label>
              <input type="text" name="type" value={newClient.type} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>Référence client :</label>
              <input type="text" name="reference" value={newClient.reference} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>Nom de l'entreprise :</label>
              <input type="text" name="entreprise" value={newClient.entreprise} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>Prénom :</label>
              <input type="text" name="prenom" value={newClient.prenom} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>Nom de famille :</label>
              <input type="text" name="nom" value={newClient.nom} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>Email :</label>
              <input type="text" name="email" value={newClient.email} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>Numéro de téléphone :</label>
              <input type="text" name="numTel" value={newClient.numTel} onChange={handleChange} />
            </div>
            {/* Ajouter d'autres champs du formulaire ici */}
            <div className="form-group">
              <button onClick={handleCancel} className="blue-button">Annuler</button>
              <button className="blue-button">Enregistrer</button>
            </div>
          </div>
        ) : (
          <div>
            {/* Contenu principal de la page */}
            <h1>Clients</h1>
            <p>Clients enregistrés avec votre abonnement : 0/5</p>
            {/* Tableau des clients */}
            <table>
              <thead>
                <tr>
                  <th>Numéro</th>
                  <th>Client</th>
                  <th>Factures en attente</th>
                  <th>Factures payées</th>
                </tr>
              </thead>
              <tbody>
                {/* Lignes de clients */}
                <tr>
                  <td><input type="text" defaultValue="1" /></td>
                  <td><input type="text" defaultValue="Nom du client" /></td>
                  <td><input type="text" defaultValue="0" /></td>
                  <td><input type="text" defaultValue="0" /></td>
                </tr>
                {/* Ajouter d'autres lignes ici */}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Client;
