import React, { useState } from 'react';

function MonCompte() {
  const [typeFacture, setTypeFacture] = useState('Facture standard');
  const [afficherColonnes, setAfficherColonnes] = useState('Colonnes par défaut');
  const [devise, setDevise] = useState('EUR');
  const [langueFacturation, setLangueFacturation] = useState('Français');
  const [typeProduit, setTypeProduit] = useState('Produit standard');
  const [parametresRemise, setParametresRemise] = useState('');
  const [referenceClient, setReferenceClient] = useState('');

  return (
    <div>
      <div className="field">
        <label>Type de facture</label>
        <select value={typeFacture} onChange={e => setTypeFacture(e.target.value)}>
          <option>Facture standard</option>
          <option>Facture pro forma</option>
          <option>Facture d'acompte</option>
        </select>
      </div>
      <div className="field">
        <label>Afficher colonnes</label>
        <select value={afficherColonnes} onChange={e => setAfficherColonnes(e.target.value)}>
          <option>Colonnes par défaut</option>
          <option>Personnalisé</option>
        </select>
      </div>
      <div className="field">
        <label>Devise</label>
        <select value={devise} onChange={e => setDevise(e.target.value)}>
          <option>EUR</option>
          <option>USD</option>
          <option>GBP</option>
          <option>...</option>
        </select>
      </div>
      <div className="field">
        <label>Langue de facturation</label>
        <select value={langueFacturation} onChange={e => setLangueFacturation(e.target.value)}>
          <option>Français</option>
          <option>Anglais</option>
          <option>Espagnol</option>
          <option>...</option>
        </select>
      </div>
      <div className="field">
        <label>Type de produit</label>
        <select value={typeProduit} onChange={e => setTypeProduit(e.target.value)}>
          <option>Produit standard</option>
          <option>Produit personnalisé</option>
          <option>Service</option>
        </select>
      </div>
      <div className="field">
        <label>Paramètres de remise</label>
        <input type="text" value={parametresRemise} onChange={e => setParametresRemise(e.target.value)} placeholder="Entrez les paramètres de remise" />
      </div>
      <div className="field">
        <label>Référence client</label>
        <input type="text" value={referenceClient} onChange={e => setReferenceClient(e.target.value)} placeholder="Entrez la référence client par défaut" />
      </div>
      <button>Enregistrer</button>
    </div>
  );
}

export default MonCompte;
