import React, { useState } from 'react';
import './Client.css';

const Client = () => {
  const [showNewClientForm, setShowNewClientForm] = useState(false);
  const [newClient, setNewClient] = useState({
    type: '',
    reference: '',
    entreprise: '',
    prenom: '',
    nom: '',
    email: '',
    numTel: ''
  });
  const [clients, setClients] = useState([]); 
  const [clientCount, setClientCount] = useState(0); 
  const [selectedClient, setSelectedClient] = useState(null);
  const [showProductDetails, setShowProductDetails] = useState(false);

  const handleNewClient = () => {
    setShowNewClientForm(true);
  };

  const handleCancel = () => {
    setShowNewClientForm(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewClient(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSaveClient = () => {
    setClientCount(prevCount => prevCount + 1);

    const client = {
      numero: clientCount + 1,
      nom: newClient.nom,
      prenom: newClient.prenom,
      facturesEnAttente: 0,
      facturesPayees: 0 
    };

    setClients(prevClients => [...prevClients, client]);

    setNewClient({
      type: '',
      reference: '',
      entreprise: '',
      prenom: '',
      nom: '',
      email: '',
      numTel: ''
    });

    setShowNewClientForm(false);
  };

  const handleClientClick = (client) => {
    setSelectedClient(client);
  };

  const handleEditClient = () => {
    // Code to handle editing client details
  };

  const handleDeleteClient = () => {
    // Code to handle deleting a client
  };

  const handleProductClick = () => {
    setShowProductDetails(!showProductDetails);
  };

  return (
    <div className="client-page">
      <div className="left-side">
        <div className="left-top">
          <input type="text" placeholder="Recherche..." />
          <button onClick={handleNewClient} className="green-button">Nouveau client</button>
        </div>
        <div className="main-content">
          {showNewClientForm ? (
            <div className="new-client-form">
              <h2>Nouveau client</h2>
              {/* Form inputs */}
            </div>
          ) : (
            <div>
              <h1>Clients</h1>
              {/* Client List */}
            </div>
          )}
        </div>
      </div>
      <div className="right-side">
        <h1>Mes Clients</h1>
        {/* Client Details */}
      </div>
    </div>
  );
};

export default Client;
